import axios from 'axios';
import { useAuth0 } from '@auth0/auth0-vue';
export default {
  auth0: useAuth0,
  name: 'SingleEssayView',
  data() {
    return {
      comments: ["default comments"],
      loading: true,
      errored: false,
      user: this.$auth0.user,
      isAuthenticated: this.$auth0.isAuthenticated,
      filteredComments: []
    };
  },
  mounted() {
    this.loadComments();
  },
  methods: {
    loadComments() {
      this.loading = true;
      axios.get('http://localhost:5000/essay/' + this.$route.params.id + '/comments', {
        email: this.user.email
      }).then(response => {
        console.log(response.data);
        this.comments = response.data;
      }).catch(error => {
        console.log(error);
        this.errored = true;
      }).finally(() => {
        this.loading = false;
      });
    },
    filterComments() {
      for (var i = 0; i < this.comments.length(); i++) {
        if (this.comments[i].line_number === this.$route.params.line) this.filterComments += this.comments[i];
      }
    }
  }
};
